definition.script:
pipeline {
        agent any
        stages {
            stage('Get project'){
                steps{
                   git branch: '6-PactBroker', url: 'https://github.com/morvader/Pact-Workshop'
                   sh "npm install"
                }
            }
            stage('Create Pacts') {
                steps {
                    sh "npm run generate-pact-client"
                }
            }
            stage('Publish Pacts') {
                steps {
                    sh "npm run publish-pacts-Broker"
                }
            }
        }
    }
